import { loadRemoteModule } from '@angular-architects/module-federation';
import { Component, ComponentFactoryResolver, OnInit, ViewContainerRef } from '@angular/core';

@Component({
  selector: 'mf-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css'],
})
export class AppComponent implements OnInit {
  title = 'shell';
  constructor(
    private cfr: ComponentFactoryResolver,
    private vcref: ViewContainerRef
  ) {
    fetch('');
  }

  async ngOnInit() {
    const { TestMfeComponent } = await loadRemoteModule({
      type: 'manifest',
      remoteName: 'mfe1',
      exposedModule: './TestMfe'
    })
    console.log(TestMfeComponent)

    const componentRef: any = this.vcref.createComponent(
      this.cfr.resolveComponentFactory(TestMfeComponent)
    );
    componentRef.instance.testMfe = 'p';
  }
}
